import { default as LayerCake, flatten } from 'layercake';
import { stack } from 'd3-shape';
import { scaleBand } from 'd3-scale';
import fruit from './data/fruitOrdinal.js';
import ColumnStacked from './components/ColumnStacked.html';
import AxisX from './components/AxisXScaleBand.html';
import AxisY from './components/AxisY.html';

const seriesNames = Object.keys(fruit[0]).filter(d => d !== 'year');

const stackData = stack()
  .keys(seriesNames);

const series = stackData(fruit);

function formatYTick (d) {
  if (d > 999) {
    return Math.round(d / 1000) + 'k';
  }
  return d;
}

const colors = ['#00e047', '#7ceb68', '#b7f486', '#ecfda5'];

const myCake = new LayerCake({
  padding: { top: 0, right: 0, bottom: 20, left: 20 },
  x: d => d.data.year,
  y: [0, 1],
  xScale: scaleBand().paddingInner([0.02]).round(true),
  xDomain: ['2016', '2017', '2018', '2019'],
  flatData: flatten(series),
  data: series,
  target: document.getElementById('my-chart'),
  custom: { seriesNames }
})
  .svgLayers([
    { component: AxisX, opts: { gridlines: false } },
    { component: AxisY, opts: { gridlines: false, ticks: 3, formatTick: formatYTick } },
    { component: ColumnStacked, opts: { colors } }
  ]);

myCake.render();

components.contents:
{#each $data as series, i}
  {#each series as d}
    <rect class='group-rect' data-id="{i}" x="{x(d)}" y="{y(d)[1]}" {width} height="{height(d)}"fill={fill(series.key)}></rect>
  {/each}
  }
{/each}

<script>
import { scaleOrdinal } from 'd3-scale';

export default {
  namespace: 'svg',
  computed: {
    colorScale: ({ $xScale, $custom, $data, opts }) => {
      return scaleOrdinal()
        .domain($custom.seriesNames)
        .range(opts.colors);
    },
    fill: ({ colorScale, $custom }) => {
      return seriesName => colorScale(seriesName);
    },
    x: ({ $xGet }) => {
      return (d) => {
        return $xGet(d);
      };
    },
    y: ({ $yGet }) => {
      return (d) => {
        return $yGet(d);
      };
    },
    width: ({ $xScale }) => {
      return $xScale.bandwidth();
    },
    height: ({ $height, $xScale, y }) => {
      return (d) => {
        const vals = y(d);
        return vals[0] - vals[1];
      };
    }
  }
};
</script>
